public class Results {
    public static void main(String[] args) {
        //region input
//        String polymerString = "dabAcCaCBAcCcaDA";
        String polymerString = "";
        //endregion
        System.out.println("***task 1***");
        System.out.println(reactPolymer(polymerString));

        System.out.println("***task 2***");
        System.out.println(findBestPolymerSolution(polymerString));

    }

    public static int reactPolymer(String polymerString) {
        StringBuilder polymer = new StringBuilder(polymerString);
        for (int i = 0; i < polymer.length() - 1; i++) {
            if (polymer.charAt(i) != polymer.charAt(i + 1)) {
                if (Character.toLowerCase(polymer.charAt(i)) == Character.toLowerCase(polymer.charAt(i + 1))) {
                    polymer.deleteCharAt(i + 1);
                    polymer.deleteCharAt(i);
                    i = -1; //after ++ it will be 0 and loop will start again
                }
            }
        }
        return polymer.length();
    }

    public static String improvePolymer(String polymerString, char c) {
        String letter = Character.toString(c);
        String upperLetter = Character.toString(Character.toUpperCase(c));
        polymerString = polymerString.replace(letter, "");
        return polymerString.replace(upperLetter, "");
    }

    public static int findBestPolymerSolution(String polymerString) {
        char[] alphabet = "abcdefghijklmnopqrstuvwxyz".toCharArray();
        String improvedPolymer;
        int minPolymerLength = polymerString.length();
        for (char letter : alphabet) {
            System.out.print("\r"+letter);
            improvedPolymer = improvePolymer(polymerString, letter);
            int polymerLength = reactPolymer(improvedPolymer);
            if (polymerLength<minPolymerLength) minPolymerLength = polymerLength;
        }
        return minPolymerLength;
    }
}
